def search_links_lch(response):
    links = []
    response = response.split("\n")
    for i in response:
        query = llm.predict(f"give 1 link of free resource for each topic to learn {i} CHECK THAT ALLL SITES ARE WORKING ")
        links.append(query)
        print(links)
    return links

